class SvgIconProvider(QObject):
    def __init__(self):
        super().__init__()
        self._cache_icon = {}
        self._cache_pixmap = {}

    def get_icon(self, icon: BootstrapIcons, size=QSize(16, 16)) -> QIcon:
        cache_key = hash(icon.value + str(size.width()) + str(size.height()))
        return self._svg_to_icon(cache_key, icon.value, size)

    def get_pixmap(self, icon: BootstrapIcons, size=QSize(16, 16)) -> QPixmap:
        cache_key = hash(icon.value + str(size.width()) + str(size.height()))
        self._svg_to_icon(cache_key, icon.value, size)
        return self._cache_pixmap[cache_key]

    def _svg_to_icon(self, key, svg_data: str, size: QSize = QSize(16, 16)) -> QIcon:
        if key not in self._cache_icon:
            if key not in self._cache_pixmap:
                svg_data = f"""<svg xmlns="http://www.w3.org/2000/svg" width="{size.width().real}" height="{size.height().real}" fill="currentColor" viewBox="0 0 {size.width().real} {size.height().real}">{svg_data}</svg>"""
                svg_bytes = QByteArray(svg_data.encode('utf-8'))
                renderer = QSvgRenderer(svg_bytes)
                pixmap = QPixmap(size)
                pixmap.fill(Qt.GlobalColor.transparent)
                painter = QPainter(pixmap)
                renderer.render(painter)
                painter.end()
                self._cache_pixmap[key] = pixmap
            self._cache_icon[key] = QIcon(self._cache_pixmap[key])
        return self._cache_icon[key]


icon_provider = SvgIconProvider()
